import React, { useState, useEffect } from "react";

const ComingSoon = ({ expiryTimestamp }) => {
  const [countdown, setCountdown] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });
  useEffect(() => {
    const endDate = new Date(); // Get current date/time
    endDate.setDate(endDate.getDate() + 2); // Add 2 days

    const interval = setInterval(() => {
      const now = new Date().getTime();
      const distance = endDate.getTime() - now;

      const days = Math.floor(distance / (1000 * 60 * 60 * 24));
      const hours = Math.floor(
        (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
      );
      const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((distance % (1000 * 60)) / 1000);

      setCountdown({ days, hours, minutes, seconds });

      if (distance < 0) {
        clearInterval(interval);
        // Optionally, handle what happens when the countdown reaches zero
      }
    }, 1000); // Update every second

    return () => clearInterval(interval); // Cleanup the interval on component unmount
  }, []);
  return (
    <div className="coming_soon">
      <div>
        <div className="coming_soon_timer">
          <span id="hours">{countdown.hours} </span>
          <span id="minutes">{countdown.minutes}</span>
          <span id="seconds">{addLeadingZero(timeLeft.seconds)}</span>
        </div>
      </div>
    </div>
  );
};

export default ComingSoon;
