import React, { useState, useEffect } from "react";

const ComingSoon = ({ expiryTimestamp }) => {
  const storedExpiryTimestamp = localStorage.getItem("expiryTimestamp");
  const initialTimeLeft = calculateTimeLeft(expiryTimestamp);

  const [timeLeft, setTimeLeft] = useState(initialTimeLeft);

  useEffect(() => {
    localStorage.setItem("expiryTimestamp", expiryTimestamp);
  }, [expiryTimestamp]);

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(calculateTimeLeft(storedExpiryTimestamp));
    }, 1000);

    return () => clearTimeout(timer);
  }, [storedExpiryTimestamp]);

  function calculateTimeLeft(timestamp) {
    const difference = timestamp - new Date();
    let timeLeft = {};

    if (difference > 0) {
      timeLeft = {
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
        seconds: Math.floor((difference / 1000) % 60),
      };
    }

    return timeLeft;
  }

  const addLeadingZero = (value) => {
    return value.toString().padStart(2, "0");
  };

  return (
    <div className="coming_soon">
      <div>
        <div className="coming_soon_timer">
          <span id="hours">{addLeadingZero(timeLeft.hours)}</span>
          <span id="minutes">{addLeadingZero(timeLeft.minutes)}</span>
          <span id="seconds">{addLeadingZero(timeLeft.seconds)}</span>
        </div>
      </div>
    </div>
  );
};

export default ComingSoon;
