import React, { useState, useEffect } from "react";
import BirdsAudio from "../assets/Audio/mixkit-birds-in-the-spring-forest.wav";
import ClockAudio from "../assets/Audio/clock-tick-tock-sound.mp3";

const ComingSoon = ({ expiryTimestamp }) => {
  const storedTimeLeft = localStorage.getItem("timeLeft");
  const initialTimeLeft = storedTimeLeft
    ? JSON.parse(storedTimeLeft)
    : calculateTimeLeft();

  const [timeLeft, setTimeLeft] = useState(initialTimeLeft);
  const [audioStarted, setAudioStarted] = useState(false);

  useEffect(() => {
    localStorage.setItem("timeLeft", JSON.stringify(timeLeft));
  }, [timeLeft]);

  useEffect(() => {
    const birdsAudioElement = new Audio(BirdsAudio);
    const clockAudioElement = new Audio(ClockAudio);

    birdsAudioElement.loop = true;
    clockAudioElement.loop = true;

    const playBirdsAudio = () => {
      birdsAudioElement.play();
    };

    const playClockAudio = () => {
      clockAudioElement.play();
    };

    // Check if the audio can be played without user interaction
    const canAutoplay =
      birdsAudioElement.play() !== undefined &&
      clockAudioElement.play() !== undefined;

    if (canAutoplay) {
      playBirdsAudio();
      playClockAudio();
      setAudioStarted(true);
    }

    return () => {
      birdsAudioElement.pause();
      clockAudioElement.pause();
    };
  }, []);

  useEffect(() => {
    if (audioStarted) {
      const timer = setTimeout(() => {
        setTimeLeft(calculateTimeLeft());
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [audioStarted, timeLeft]);

  useEffect(() => {
    // Check if audio was started before reloading
    const storedAudioStarted = localStorage.getItem("audioStarted");
    if (storedAudioStarted === "true") {
      setAudioStarted(true);
    }
  }, []);

  window.addEventListener("beforeunload", () => {
    localStorage.setItem("audioStarted", audioStarted.toString());
  });

  function calculateTimeLeft() {
    const difference = expiryTimestamp - new Date();
    let timeLeft = {};

    if (difference > 0) {
      timeLeft = {
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
        seconds: Math.floor((difference / 1000) % 60),
      };
    }

    return timeLeft;
  }

  const addLeadingZero = (value) => {
    return value.toString().padStart(2, "0");
  };

  return (
    <div className="coming_soon">
      <div>
        <div className="coming_soon_timer">
          <span id="hours">{addLeadingZero(timeLeft.hours)}</span>
          <span id="minutes">{addLeadingZero(timeLeft.minutes)}</span>
          <span id="seconds">{addLeadingZero(timeLeft.seconds)}</span>
        </div>
        {!audioStarted && (
          <button id="play_button" onClick={setAudioStarted.bind(null, true)}>
            Play Music
          </button>
        )}
      </div>
    </div>
  );
};

export default ComingSoon;
