import React, { useState, useEffect } from "react";

const ComingSoon = ({ expiryTimestamp }) => {
  const storedTimeLeft = localStorage.getItem("timeLeft");
  const initialTimeLeft = storedTimeLeft
    ? JSON.parse(storedTimeLeft)
    : calculateTimeLeft(expiryTimestamp); // Calculate initial time left based on expiry timestamp

  const [timeLeft, setTimeLeft] = useState(initialTimeLeft);

  useEffect(() => {
    const timer = setTimeout(() => {
      const updatedTimeLeft = calculateTimeLeft(expiryTimestamp); // Calculate updated time left
      setTimeLeft(updatedTimeLeft);
      localStorage.setItem("timeLeft", JSON.stringify(updatedTimeLeft)); // Update time left in local storage
    }, 1000);

    return () => clearTimeout(timer);
  }, [expiryTimestamp]);

  useEffect(() => {
    const storedExpiryTimestamp = localStorage.getItem("expiryTimestamp");
    if (
      !storedExpiryTimestamp ||
      storedExpiryTimestamp !== expiryTimestamp.toString()
    ) {
      localStorage.setItem("expiryTimestamp", expiryTimestamp.toString());
    }
  }, [expiryTimestamp]);

  function calculateTimeLeft(timestamp) {
    const difference = timestamp - new Date();
    if (difference <= 0) {
      return { hours: 0, minutes: 0, seconds: 0 }; // Timer expired
    }

    return {
      hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((difference / 1000 / 60) % 60),
      seconds: Math.floor((difference / 1000) % 60),
    };
  }

  const addLeadingZero = (value) => {
    return value.toString().padStart(2, "0");
  };

  return (
    <div className="coming_soon">
      <div>
        <div className="coming_soon_timer">
          <span id="hours">{addLeadingZero(timeLeft.hours)}</span>
          <span id="minutes">{addLeadingZero(timeLeft.minutes)}</span>
          <span id="seconds">{addLeadingZero(timeLeft.seconds)}</span>
        </div>
      </div>
    </div>
  );
};

export default ComingSoon;
